perspectives.style:
@settings {
  template: causal-loop;
  theme: light;
  element-flag: "Beneficiaries" with neon2;
  layout: force;
  layout-preset: dense;
}

@controls {
  bottom {
    showcase {
      target: element;
      by: "result level";
      as: buttons;
      multiple: true;
      default: select-none;
      mode: normal;
    }
  }
}

@settings {
  template: radar;
  radar-rings: INTENT /(Resource Allocation and Sustainable Development), DESIGN (Information Systems for Decision Making), FEEDBACKS (Energy for Value), PARAMETERS;
  radar-axes: INDICATOR, SYSTEMS, INNOVATION, STRATEGY, ACTION;
  element-scale: scale("Technologies", 2, 3);
  bridge: 0;
  element-color: categorize("Result Level", neon2);
  cluster: ["element type"="Sustainable Development"] by "Element Type", ["element type"="Humane"] by "Element Type", ["element type"="Resource Scarcity"] by "Element Type";
}

@controls {
  bottom {
    showcase {
      target: element;
      by: "element type";
      as: buttons;
      multiple: true;
      default: show-all;
      mode: normal;
    }
  }
}

@settings {
  template: systems;
  radar-axes: Prolem, Input, Output, Outcome, Impact;
  radar-rings: Intent, Design, Feedback, Parameters;
  radar-ring-size: 525;
  radar-ring-size-inner: 425;
  radar: true;
  radar-font-size: 30;
  radar-ring-opacity: 0.05;
  cluster: ["element type"="Sustainable Development"] by "Element Type", ["element type"="Humane"] by "Element Type", ["element type"="Resource Scarcity"] by "Element Type";
  theme: light;
  element-color: categorize("Element Type", neon2);
}
